import './styles.scss'

export const PromoCard = (props) => {
    return (
        <div className="promo">
            <img src={props.image} className="promoCard" alt="PromoCard" />
            <div className="texts">
                <h1>Підберемо за 30ха саме те що потрібно для вас!</h1>
                <p>Допоможемо підібрати ювелірні вироби саме для вас всього за пів години! проте якщо у нас не вийде МЕГА знижка для вас.</p>
            </div>
        </div>
    );
};
